import React, { Component } from 'react';
import TodoListTemplate from './components/TodoListTemplate';
import TodoItemList from './components/TodoItemList';
import Form from './components/Form';
import './index.css';

class App extends Component {

  id=3 //이미 0,1,2가 존재하므로 3으로 설정

  state={
    input: '',
    todos: [
      {id:0, text:'리액트 공부', checked:false},
      {id:1, text:'리액트 적용', checked:true},
      {id:2, text:'리액트 소개', checked:false}
    ]
  }

  handleChange = (e) =>{
    this.setState({
      input:e.target.value
    });
  }

  handleCreate = () =>{
    const {input, todos} = this.state;
    console.log(input);
    if(input === ' ') return;
    this.setState({
      input:'',
      todos:todos.concat({
        id : this.id++,
        text: input,
        checked : false
      })
    });
  }

  handleKeyPress = (e) =>{
    if(e.key === 'Enter'){
      this.handleCreate();
    }
  }

  handleToggle = (id) =>{
    const {todos} = this.state;

    // 파라미터로 받은 id를 가지고 몇번째 아이템인지 찾습니다. 
    const index = todos.findIndex(todo => todo.id === id);
    const selected = todos[index]; //선택한 객체

    const nextTodos = [...todos]; //배열복사

    //기존의 값들을 복사하고, checked값을 덮어쓰기
    nextTodos[index] = {
      ...selected,
      checked: !selected.checked
    };

    this.setState({
      todos : nextTodos
    })
  }

  handleRomve = (id) => {
    const {todos} = this.state;
    this.setState({
      todos : todos.filter(todo => todo.id !== id)
    });
  }

  render(){
    const {input, todos} = this.state;
    const {
      handleChange,
      handleCreate,
      handleKeyPress,
      handleToggle,
      handleRomve
    } = this;

    return (
      <TodoListTemplate form={(
      <Form
        value={input}
        onKeyPress={handleKeyPress}
        onChange={handleChange}
        onCreate={handleCreate}
      />
      )}>
        <TodoItemList todos={todos} onToggle={handleToggle} onRemove={handleRomve}/>
      </TodoListTemplate>
    );
  }

}

export default App;
